import osimport shutilfrom subprocess import runCONTCAR_LOC = "..//agbii4-cubic-cryst-444-gga-contcars"print("Wait - this will take a while...")for num, contcar in enumerate(os.listdir(CONTCAR_LOC)):    genNum = contcar.split('-')[1]    print("working on " + str(genNum) + "... (" + str(num) + '/' + str(len(os.listdir(CONTCAR_LOC))) + ')')    ##Make a tmp poscar here with the gen number written to file title    shutil.copy(os.path.join(CONTCAR_LOC, contcar), "tmp")    with open("tmp", 'r') as infile:        with open("tmp_", 'w') as outfile:            for n, line in enumerate(infile):                if(n == 0):                    line = str(genNum) + "\n"                outfile.write(line)            outfile.close()        infile.close()    #Execute bond counting code    run(".\\bondInfoCsv.exe -i tmp_ -b bondDataInfo -o bondInfo.csv", shell=True)    #Clean up    os.remove("tmp")    os.remove("tmp_")